import api from './api';

export const ACTION_TYPES = {
    CREATE:'CREATE',
    UPDATE:'UPDATE',
    DELETE:'DELETE',
    FETCH_ALL:'FETCH_ALL'
}

export const fetchAll = () => dispatch => {
    api.blogMessage().fetchAll()
        .then(res => {
            dispatch({
                type:ACTION_TYPES.FETCH_ALL,
                payload:res.data
            })
        })
        .catch(err => console.log(err))
    
}

export const create = (data, onSuccess) => dispatch =>{
    api.blogMessage().create(data)
    .then(res =>{
        dispatch({
            type:ACTION_TYPES.CREATE,
            payload:res.data
        })
        onSuccess()
    })
    .catch(err => console.log(err))

}

export const update = (id, data, onSuccess) => dispatch =>{
    api.blogMessage().create(data)
    .then(res =>{
        dispatch({
            type:ACTION_TYPES.CREATE,
            payload:res.data
        })
    })
    .catch(err => console.log(err))

}
