import {ACTION_TYPES} from '../actions/blogMessage'
const initialState ={
    list:[]
}
//blogMessage.list
export const blogMessage = (state=initialState, action)=>{
    switch(action.type){
        case ACTION_TYPES.FETCH_ALL:
            return{
                ...state,
                list:[...action.payload]
            }
        case ACTION_TYPES.CREATE:
            return
        default:
            return state;
    }
}
