import { TextField, withStyles } from '@material-ui/core';
import React,{useEffect, useState} from 'react';

const initialFeedValues = {
    title:'',
    message:''
}

const styles = theme =>({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1)
        },
    },
    form:{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center'
    }
})

const BlogMessageForm = ({classes, ...props}) => {
    const [values, setValues] = useState(initialFeedValues)

    return(
        <form autoComplete="off" noValidate className={`$classes.root`}>
            <TextField name="title" variant="outlined" label="Title" fullWidth value={values.title} />
            <TextField name="message" variant="outlined" label="Message" fullWidth multiline rows="5" value={values.message} />
        </form>
    );
}

export default withStyles(styles)(BlogMessageForm);
