import { Typography } from '@material-ui/core';
import { ListItemText } from '@material-ui/core';
import { Grid, Paper, withStyles, List, ListItem, Divider, Button } from '@material-ui/core';
import React,{useEffect, useState, Fragment} from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions/blogMessage';
import BlogMessageForm from "./BlogMessageForm";

const styles = theme => ({
    paper :{
        margin: theme.spacing(3),
        padding: theme.spacing(2)
    },
    smMargin:{
        margin: theme.spacing(1)
    },
    actionDiv:{
        textAlign:"center"
    }
})

const BlogMessages = ({classes, ...props}) => {
    const [currentId, setCurrentId] = use

    useEffect(()=>{
        props.fetchAllBlogMessages()
    },[])//DidMount

    return (
        <Grid container>
            <Grid item xs={5}>
                <Paper className={classes.paper}>
                    <BlogMessageForm />
                </Paper>
            </Grid>
            <Grid item xs={7}>
                <Paper className={classes.paper}>
                    <List>
                        {
                            props.blogMessageList.map((record, index) =>{
                                return(
                                    <Fragment key={index}>
                                        <ListItem>
                                            <ListItemText>
                                                <Typography variant="h5">
                                                    {record.title}
                                                </Typography>
                                                <div>
                                                    {record.message}
                                                </div>
                                                <div className={classes.actionDiv}>
                                                    <Button variant="contained" color="primary" size="small" className={classes.smMargin}>Edit</Button>
                                                    <Button variant="contained" color="secondary" size="small" className={classes.smMargin}>Delete</Button>
                                                </div>
                                            </ListItemText>
                                        </ListItem>
                                        <Divider component="li" />
                                    </Fragment>
                                )
                            })
                        }
                    </List>
                </Paper>
            </Grid>
        </Grid>
    );
}

const mapStateToProps = state => ({
    blogMessageList: state.blogMessage.list
})

const mapActionToProps ={
    fetchAllBlogMessages : actions.fetchAll
}
//props.fetchAllBlogMessages

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles)(BlogMessages));
