import { TextField, withStyles, Button } from '@material-ui/core';
import React,{useEffect, useState} from 'react';
import useForm from "./useForm";

const initialFeedValues = {
    title:'',
    message:''
}

const styles = theme =>({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1)
        },
    },
    form:{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center'
    },
    postBtn:{
        backgroundColor: '#00ADB5',
        '&:hover': {
            backgroundColor: '#A6E3E9',
            borderColor: '#00ADB5',
            color: '#00ADB5'
        }
    }
})

const BlogMessageForm = ({classes, ...props}) => {

    const validate = () =>{
        let temp = {...errors}
        temp.title = values.title?"":"This field is required"
    }

    var {values, setValues, errors, setErrors, handleInputChange} = useForm(initialFeedValues)
    const handleSubmit = e =>{
        e.preventDefault()
        console.log(values)
    }
    return(
        <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`}
        onSubmit={handleSubmit}>
            <TextField name="title" variant="outlined" label="Title" fullWidth value={values.title} onChange={handleInputChange} />
            <TextField name="message" variant="outlined" label="Message" fullWidth multiline rows="5" value={values.message} onChange={handleInputChange} />
            <Button variant="contained" color="primary" size="large" type="submit" className={classes.postBtn}>Submit</Button>
        </form>
    );
}

export default withStyles(styles)(BlogMessageForm);
