import { Grid } from '@material-ui/core';
import React,{useEffect, useState} from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions/blogMessage';
import BlogMessageForm from "./BlogMessageForm";

const BlogMessages = (props) => {
    //const [X, setX] = useState(0)
    //setX(5)

    useEffect(()=>{
        props.fetchAllBlogMessages()
    },[])//DidMount

    return (
        <Grid container>
            <Grid item xs={5}>
                <Paper></Paper>
                <BlogMessageForm />
            </Grid>
            <Grid item xs={7}>
                <div>list of notes</div>
            </Grid>
        </Grid>
    );
}

const mapStateToProps = state => ({
    blogMessageList: state.blogMessage.list
})

const mapActionToProps ={
    fetchAllBlogMessages : actions.fetchAll
}
//props.fetchAllBlogMessages

export default connect(mapStateToProps, mapActionToProps)(BlogMessages);
